ValidationRule.errorConditionFormula:
IF(
    ISNEW(),
    IF(
        ISBLANK(Numero_de_poliza__c),
        false,
        NOT(
            REGEX( 
                Numero_de_poliza__c ,
                "[0-9]{2}-[0-9]{2}-[0-9]{8}"
            )
        )
    ),
    IF(
        AND(
            ISCHANGED(StageName),
            ISPICKVAL(StageName, "Closed Won")
        ),
        IF(
            OR(
                ISBLANK(Numero_de_poliza__c),
                NOT(
                    REGEX( 
                        Numero_de_poliza__c ,
                        "[0-9]{2}-[0-9]{2}-[0-9]{8}"
                    )
                )
            ),
            true,
            false
        ),
        IF(
            ISBLANK(Numero_de_poliza__c),
            false,
            NOT(
                REGEX( 
                    Numero_de_poliza__c ,
                    "[0-9]{2}-[0-9]{2}-[0-9]{8}"
                )
            )
        )
    )
)
